  `views` int(6) NOT NULL DEFAULT 0,
  `likes` int(5) NOT NULL DEFAULT 0
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_swedish_ci;

ALTER TABLE `_citas`
  ADD PRIMARY KEY (`id`);

